DROP TRIGGER IF EXISTS `entretenerse`.`attributes_fulltext_trigger`;
DELIMITER //
CREATE TRIGGER attributes_fulltext_trigger AFTER UPDATE ON attributes
FOR EACH ROW BEGIN
  UPDATE  `entretenerse`.`occurrences_attributes` SET attribute_id = NEW.parent_id WHERE attribute_id = OLD.parent_id AND occurrence_id IN (SELECT occurrence_id FROM (SELECT occurrence_id FROM `entretenerse`.`occurrences_attributes` WHERE attribute_id = NEW.id) changed_occ);
  UPDATE  `entretenerse`.`events_attributes` SET attribute_id = NEW.parent_id WHERE attribute_id = OLD.parent_id AND event_id IN (SELECT event_id FROM (SELECT event_id FROM `entretenerse`.`events_attributes` WHERE attribute_id = NEW.id) changed_ev);
END; //

